import{_ as n,c as s,o as i,ag as l}from"./chunks/framework.C4nOkCZI.js";const d=JSON.parse('{"title":"XiHan.BasicApp 系统概述","description":"","frontmatter":{},"headers":[],"relativePath":"cosmos/basic-app/overview.md","filePath":"cosmos/basic-app/overview.md","lastUpdated":1747783171000}'),r={name:"cosmos/basic-app/overview.md"};function t(o,a,e,p,g,h){return i(),s("div",null,[...a[0]||(a[0]=[l(`<h1 id="xihan-basicapp-系统概述" tabindex="-1">XiHan.BasicApp 系统概述 <a class="header-anchor" href="#xihan-basicapp-系统概述" aria-label="Permalink to &quot;XiHan.BasicApp 系统概述&quot;">​</a></h1><p>XiHan.BasicApp 是一个基于 XiHan.Framework 和 XiHan.UI 构建的全功能企业级管理系统，提供了丰富的企业应用场景的基础功能和业务模板，可直接用于生产或作为二次开发的基础。</p><h2 id="系统特点" tabindex="-1">系统特点 <a class="header-anchor" href="#系统特点" aria-label="Permalink to &quot;系统特点&quot;">​</a></h2><ul><li><strong>全栈方案</strong>：集成前后端完整技术栈，实现无缝协作</li><li><strong>模块化架构</strong>：采用模块化设计，各功能模块可插拔，便于扩展和定制</li><li><strong>企业级功能</strong>：内置丰富的企业应用场景所需的基础功能</li><li><strong>安全可靠</strong>：集成完善的安全防护措施，保障系统和数据安全</li><li><strong>性能优化</strong>：针对大数据量和高并发场景进行性能优化</li><li><strong>开箱即用</strong>：提供完整的部署方案，支持快速上线和使用</li></ul><h2 id="系统架构" tabindex="-1">系统架构 <a class="header-anchor" href="#系统架构" aria-label="Permalink to &quot;系统架构&quot;">​</a></h2><p>XiHan.BasicApp 采用前后端分离的架构设计，整体架构如下：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>+-------------------+      +---------------------+</span></span>
<span class="line"><span>|     前端应用       |      |       后端服务       |</span></span>
<span class="line"><span>| (XiHan.UI + Vue)  |&lt;----&gt;| (XiHan.Framework)   |</span></span>
<span class="line"><span>+-------------------+      +---------------------+</span></span>
<span class="line"><span>         |                           |</span></span>
<span class="line"><span>+-------------------+      +---------------------+</span></span>
<span class="line"><span>|    状态管理        |      |     业务逻辑层       |</span></span>
<span class="line"><span>|    (Pinia)        |      |   (Services)        |</span></span>
<span class="line"><span>+-------------------+      +---------------------+</span></span>
<span class="line"><span>         |                           |</span></span>
<span class="line"><span>+-------------------+      +---------------------+</span></span>
<span class="line"><span>|   路由与页面       |      |     数据访问层       |</span></span>
<span class="line"><span>| (Vue Router)      |      |  (Repositories)     |</span></span>
<span class="line"><span>+-------------------+      +---------------------+</span></span>
<span class="line"><span>                                     |</span></span>
<span class="line"><span>                            +---------------------+</span></span>
<span class="line"><span>                            |       数据库         |</span></span>
<span class="line"><span>                            | (SQL/NoSQL/Cache)   |</span></span>
<span class="line"><span>                            +---------------------+</span></span></code></pre></div><h2 id="功能模块" tabindex="-1">功能模块 <a class="header-anchor" href="#功能模块" aria-label="Permalink to &quot;功能模块&quot;">​</a></h2><p>XiHan.BasicApp 包含以下核心功能模块：</p><h3 id="系统管理" tabindex="-1">系统管理 <a class="header-anchor" href="#系统管理" aria-label="Permalink to &quot;系统管理&quot;">​</a></h3><ul><li><strong>用户管理</strong>：用户信息管理、用户状态控制</li><li><strong>角色管理</strong>：角色创建与维护、角色权限分配</li><li><strong>权限管理</strong>：细粒度的功能和数据权限控制</li><li><strong>菜单管理</strong>：动态菜单配置，支持多级菜单</li><li><strong>部门管理</strong>：组织架构管理，支持树形结构</li><li><strong>岗位管理</strong>：岗位设置与人员分配</li></ul><h3 id="系统监控" tabindex="-1">系统监控 <a class="header-anchor" href="#系统监控" aria-label="Permalink to &quot;系统监控&quot;">​</a></h3><ul><li><strong>操作日志</strong>：记录用户操作，支持查询和回溯</li><li><strong>登录日志</strong>：记录用户登录信息，监控异常登录</li><li><strong>服务监控</strong>：监控服务器性能和资源使用情况</li><li><strong>缓存监控</strong>：实时监控缓存使用情况和命中率</li></ul><h3 id="系统工具" tabindex="-1">系统工具 <a class="header-anchor" href="#系统工具" aria-label="Permalink to &quot;系统工具&quot;">​</a></h3><ul><li><strong>数据字典</strong>：系统中常用的数据字典管理</li><li><strong>参数配置</strong>：动态参数配置，支持热更新</li><li><strong>代码生成</strong>：根据数据库表结构生成前后端代码</li><li><strong>系统接口</strong>：系统 API 接口文档和测试工具</li></ul><h3 id="个人中心" tabindex="-1">个人中心 <a class="header-anchor" href="#个人中心" aria-label="Permalink to &quot;个人中心&quot;">​</a></h3><ul><li><strong>个人信息</strong>：查看和修改个人资料</li><li><strong>修改密码</strong>：安全的密码修改功能</li><li><strong>操作日志</strong>：查看个人操作记录</li><li><strong>消息中心</strong>：系统通知和消息管理</li></ul><h2 id="技术栈" tabindex="-1">技术栈 <a class="header-anchor" href="#技术栈" aria-label="Permalink to &quot;技术栈&quot;">​</a></h2><h3 id="前端技术" tabindex="-1">前端技术 <a class="header-anchor" href="#前端技术" aria-label="Permalink to &quot;前端技术&quot;">​</a></h3><ul><li><strong>框架</strong>：Vue 3 + TypeScript</li><li><strong>组件库</strong>：XiHan UI</li><li><strong>状态管理</strong>：Pinia</li><li><strong>路由</strong>：Vue Router</li><li><strong>HTTP 客户端</strong>：Axios</li><li><strong>构建工具</strong>：Vite</li><li><strong>CSS 预处理器</strong>：SCSS</li></ul><h3 id="后端技术" tabindex="-1">后端技术 <a class="header-anchor" href="#后端技术" aria-label="Permalink to &quot;后端技术&quot;">​</a></h3><ul><li><strong>框架</strong>：XiHan.Framework (.NET 10)</li><li><strong>ORM</strong>：Entity Framework Core</li><li><strong>身份认证</strong>：JWT + Cookie</li><li><strong>缓存</strong>：内存缓存 + Redis</li><li><strong>日志</strong>：NLog/Serilog</li><li><strong>文档</strong>：Swagger/OpenAPI</li><li><strong>任务调度</strong>：Quartz.NET</li></ul><h3 id="数据库支持" tabindex="-1">数据库支持 <a class="header-anchor" href="#数据库支持" aria-label="Permalink to &quot;数据库支持&quot;">​</a></h3><ul><li><strong>关系型数据库</strong>：SQL Server, MySQL, PostgreSQL, SQLite</li><li><strong>NoSQL 数据库</strong>：MongoDB, Redis</li><li><strong>缓存</strong>：Redis, 内存缓存</li></ul><h2 id="部署要求" tabindex="-1">部署要求 <a class="header-anchor" href="#部署要求" aria-label="Permalink to &quot;部署要求&quot;">​</a></h2><h3 id="服务器环境" tabindex="-1">服务器环境 <a class="header-anchor" href="#服务器环境" aria-label="Permalink to &quot;服务器环境&quot;">​</a></h3><ul><li><strong>操作系统</strong>：Windows Server 2016+, Linux (Ubuntu 20.04+, CentOS 8+)</li><li><strong>CPU</strong>：2 核以上</li><li><strong>内存</strong>：4GB 以上</li><li><strong>存储</strong>：50GB 以上</li><li><strong>带宽</strong>：5Mbps 以上</li></ul><h3 id="软件环境" tabindex="-1">软件环境 <a class="header-anchor" href="#软件环境" aria-label="Permalink to &quot;软件环境&quot;">​</a></h3><ul><li><strong>.NET Runtime</strong>：.NET 10 运行时</li><li><strong>数据库</strong>：根据选择的数据库类型安装相应环境</li><li><strong>Web 服务器</strong>：IIS, Nginx, Apache</li><li><strong>Node.js</strong>：可选，用于前端构建</li></ul><h2 id="浏览器兼容性" tabindex="-1">浏览器兼容性 <a class="header-anchor" href="#浏览器兼容性" aria-label="Permalink to &quot;浏览器兼容性&quot;">​</a></h2><ul><li>Chrome (最新版)</li><li>Firefox (最新版)</li><li>Edge (最新版)</li><li>Safari (最新版)</li></ul><p>不支持 Internet Explorer。</p><h2 id="下一步" tabindex="-1">下一步 <a class="header-anchor" href="#下一步" aria-label="Permalink to &quot;下一步&quot;">​</a></h2><ul><li><a href="./deployment">快速部署</a>：了解如何部署和启动 XiHan.BasicApp</li><li><a href="./features">基础功能</a>：探索系统的基础功能模块</li><li><a href="./development">二次开发</a>：学习如何基于 XiHan.BasicApp 进行二次开发</li></ul>`,34)])])}const u=n(r,[["render",t]]);export{d as __pageData,u as default};
