import{_ as i,c as t,o,ag as e}from"./chunks/framework.KLbWPeN7.js";const g=JSON.parse('{"title":"前端-视图组件","description":"","frontmatter":{"title":"前端-视图组件","index":false,"next":{"text":"npm 包依赖关系","link":"./npm-package-dependency"}},"headers":[],"relativePath":"cosmos/ui/index.md","filePath":"cosmos/ui/index.md","lastUpdated":1743804674000}'),l={name:"cosmos/ui/index.md"};function n(s,a,r,h,u,c){return o(),t("div",null,a[0]||(a[0]=[e('<h1 id="xihan-ui-视图组件" tabindex="-1">XiHan UI 视图组件 <a class="header-anchor" href="#xihan-ui-视图组件" aria-label="Permalink to &quot;XiHan UI 视图组件&quot;">​</a></h1><p>曦寒视图组件包含以下模块：</p><h2 id="核心包-xihan-ui" tabindex="-1">核心包 (xihan-ui) <a class="header-anchor" href="#核心包-xihan-ui" aria-label="Permalink to &quot;核心包 (xihan-ui)&quot;">​</a></h2><ul><li><strong>功能</strong>：整合所有子模块，提供完整的组件库解决方案</li><li><strong>依赖</strong>：集成了所有其他子包，作为统一入口</li></ul><h2 id="组件-components" tabindex="-1">组件 (components) <a class="header-anchor" href="#组件-components" aria-label="Permalink to &quot;组件 (components)&quot;">​</a></h2><ul><li><strong>功能</strong>：提供各种 UI 组件，如按钮、表单、导航等</li><li><strong>特点</strong>：组件设计遵循原子设计系统，支持按需引入</li><li><strong>依赖</strong>：使用 hooks、directives、themes 等支持组件功能</li></ul><h2 id="工具库-utils" tabindex="-1">工具库 (utils) <a class="header-anchor" href="#工具库-utils" aria-label="Permalink to &quot;工具库 (utils)&quot;">​</a></h2><ul><li><strong>功能</strong>：提供基础工具函数，分为核心、浏览器、格式化、Vue、安全、CSS 等模块</li><li><strong>特点</strong>：轻量级、高效，支持 Tree Shaking</li></ul><h2 id="主题系统-themes" tabindex="-1">主题系统 (themes) <a class="header-anchor" href="#主题系统-themes" aria-label="Permalink to &quot;主题系统 (themes)&quot;">​</a></h2><ul><li><strong>功能</strong>：基于 CSS 变量的主题引擎，支持明暗模式切换</li><li><strong>特点</strong>：可扩展的设计令牌系统，支持自定义主题</li></ul><h2 id="钩子函数-hooks" tabindex="-1">钩子函数 (hooks) <a class="header-anchor" href="#钩子函数-hooks" aria-label="Permalink to &quot;钩子函数 (hooks)&quot;">​</a></h2><ul><li><strong>功能</strong>：提供可复用的组合式函数，如 useForm、useValidation 等</li><li><strong>特点</strong>：基于 Vue Composition API，增强组件逻辑复用</li></ul><h2 id="指令-directives" tabindex="-1">指令 (directives) <a class="header-anchor" href="#指令-directives" aria-label="Permalink to &quot;指令 (directives)&quot;">​</a></h2><ul><li><strong>功能</strong>：提供自定义指令，如 v-click-outside、v-resize 等</li><li><strong>特点</strong>：增强 DOM 交互能力，简化常见操作</li></ul><h2 id="图标-icons" tabindex="-1">图标 (icons) <a class="header-anchor" href="#图标-icons" aria-label="Permalink to &quot;图标 (icons)&quot;">​</a></h2><ul><li><strong>功能</strong>：提供统一的图标系统</li><li><strong>特点</strong>：支持按需加载和自定义颜色、大小</li></ul><h2 id="本地化-locales" tabindex="-1">本地化 (locales) <a class="header-anchor" href="#本地化-locales" aria-label="Permalink to &quot;本地化 (locales)&quot;">​</a></h2><ul><li><strong>功能</strong>：提供国际化解决方案，支持多语言切换</li><li><strong>特点</strong>：内置中英文等常用语言包，支持自定义扩展</li></ul><h2 id="插件-plugins" tabindex="-1">插件 (plugins) <a class="header-anchor" href="#插件-plugins" aria-label="Permalink to &quot;插件 (plugins)&quot;">​</a></h2><ul><li><strong>功能</strong>：提供全局功能扩展，如通知、消息等</li><li><strong>特点</strong>：可独立使用的轻量级插件系统</li></ul><h2 id="常量-constants" tabindex="-1">常量 (constants) <a class="header-anchor" href="#常量-constants" aria-label="Permalink to &quot;常量 (constants)&quot;">​</a></h2><ul><li><strong>功能</strong>：提供全局常量定义，如尺寸、状态等</li><li><strong>特点</strong>：确保设计一致性和代码统一性</li></ul><p>每个模块都遵循相同的构建和发布标准，支持 ESM 和 UMD 双模式输出，确保了组件库的可扩展性和可维护性。整体架构采用了微内核设计，通过依赖注入和插件系统实现高度解耦。</p>',23)]))}const m=i(l,[["render",n]]);export{g as __pageData,m as default};
